// Initialize agent context
const input = $json;
const startTime = Date.now();
const costs = { firecrawl: 0, openrouter: 0, total: 0 };

return [{
  json: {
    property_id: input.property_id,
    property_address: input.property_address,
    county: input.county,
    assessor_url: input.assessor_url,
    planning_url: input.planning_url,
    building_url: input.building_url,
    recorder_url: input.recorder_url,
    code_enforcement_url: input.code_enforcement_url,
    email: input.email,
    start_time: startTime,
    costs: costs,
    documents_retrieved: [],
    agent_context: `You are a CRE research agent specializing in municipal property records. Your task is to retrieve comprehensive property information including:
1. Property assessment and value
2. Property tax records
3. Zoning certificates
4. Building permits
5. Code violations
6. Liens and encumbrances
7. Ownership information
8. Property details

Property: ${input.property_address}
County: ${input.county}

Use the available tools strategically - start with search to find relevant pages, then scrape specific pages, or crawl entire sections if needed.`
  }
}];

// Process agent results and extract data
const agentOutput = $json.output || $json.text || '';
const propertyId = $('Initialize Agent Context').item.json.property_id;
const propertyAddress = $('Initialize Agent Context').item.json.property_address;

// Extract structured data using LLM
const extractionPrompt = `Extract structured property data from the following text. Return JSON with these fields:
{
  "document_type": "assessment|tax|zoning|permit|violation|lien|ownership|details",
  "assessed_value": number or null,
  "tax_amount": number or null,
  "zoning_code": string or null,
  "permit_count": number or null,
  "violation_count": number or null,
  "lien_amount": number or null,
  "owner_name": string or null,
  "property_details": object or null,
  "confidence_score": number (0-1),
  "extracted_data": object
}

Text: ${agentOutput.substring(0, 3000)}`;

return [{
  json: {
    property_id: propertyId,
    property_address: propertyAddress,
    raw_output: agentOutput,
    extraction_prompt: extractionPrompt,
    needs_extraction: true
  }
}];

// Validate and save extracted data
const extractedData = JSON.parse($json.output || $json.text || '{}');
const propertyId = $('Initialize Agent Context').item.json.property_id;
const startTime = $('Initialize Agent Context').item.json.start_time;

// Validate extracted data
if (!extractedData.document_type || extractedData.confidence_score < 0.5) {
  return [{ json: { skip: true, reason: 'Low confidence or missing document type' } }];
}

// Calculate cost (estimate)
const costs = $('Initialize Agent Context').item.json.costs;
costs.firecrawl += 0.01; // Estimate per operation
costs.openrouter += 0.002; // Estimate per LLM call
costs.total = costs.firecrawl + costs.openrouter;

const documentData = {
  property_id: propertyId,
  document_type: extractedData.document_type,
  document_category: 'municipal',
  source: 'agent_retrieval',
  extracted_data: extractedData.extracted_data || extractedData,
  confidence_score: extractedData.confidence_score,
  status: extractedData.confidence_score >= 0.7 ? 'validated' : 'needs_review'
};

return [{
  json: {
    ...documentData,
    costs: costs,
    execution_time_ms: Date.now() - startTime
  }
}];

// Aggregate results
const allItems = $input.all();
const documents = allItems.filter(i => !i.json.skip).map(i => i.json.document_type);
const totalCosts = allItems.reduce((acc, item) => {
  if (item.json.costs) {
    acc.firecrawl += item.json.costs.firecrawl || 0;
    acc.openrouter += item.json.costs.openrouter || 0;
  }
  return acc;
}, { firecrawl: 0, openrouter: 0, total: 0 });

totalCosts.total = totalCosts.firecrawl + totalCosts.openrouter;

const propertyId = $('Initialize Agent Context').item.json.property_id;
const startTime = $('Initialize Agent Context').item.json.start_time;

return [{
  json: {
    property_id: propertyId,
    status: 'success',
    documents_retrieved: documents.length,
    document_types: documents,
    costs: totalCosts,
    execution_time_ms: Date.now() - startTime,
    branch: 'municipal'
  }
}];
