// Initialize agent context for title records
const input = $json;
const startTime = Date.now();
const costs = { firecrawl: 0, openrouter: 0, total: 0 };

return [{
  json: {
    property_id: input.property_id,
    property_address: input.property_address,
    recorder_url: input.recorder_url,
    email: input.email,
    start_time: startTime,
    costs: costs,
    documents_retrieved: [],
    agent_context: `You are a CRE research agent specializing in title and deed records.

PROPERTY: ${input.property_address}
RECORDER: ${input.recorder_url}

ALL TITLE RECORDS AT COUNTY RECORDER:
• Current Deed → ${input.recorder_url}
  Get: Owner, deed type, deed date, grantor/grantee
• Historical Ownership → ${input.recorder_url}
  Get: Previous owners, transfer dates
• Liens & Mortgages → ${input.recorder_url}
  Get: Outstanding liens, amounts, mortgage records
• UCC Filings → ${input.recorder_url}
  Get: UCC numbers, secured party

STRATEGY: Search by address to get PIN, then use PIN for accurate document searches.`
  }
}];

// Process agent output for title data
const agentOutput = $json.output || $json.text || '';
const propertyId = $('Initialize Agent Context').item.json.property_id;
const extractionPrompt = `Extract structured title/deed data from the following text. Return ONLY valid JSON with these exact fields:
{
  "document_type": "deed|ucc_filing|lien|mortgage",
  "owner_name": string or null,
  "deed_date": string or null,
  "deed_type": string or null,
  "grantor": string or null,
  "grantee": string or null,
  "ucc_filing_number": string or null,
  "lien_amount": number or null,
  "mortgage_amount": number or null,
  "confidence_score": number between 0 and 1,
  "extracted_data": object with all extracted information
}

Agent Output:
${agentOutput.substring(0, 3000)}`;

return [{
  json: {
    property_id: propertyId,
    extraction_prompt: extractionPrompt,
    needs_extraction: true
  }
}];

// Validate and prepare title data for saving
const llmResponse = $json.message?.content || $json.content || $json.output || $json.text || '{}';
let extractedData;

try {
  extractedData = JSON.parse(llmResponse);
} catch (e) {
  console.log('Failed to parse LLM response:', e);
  return [{ json: { skip: true, reason: 'Invalid JSON from LLM', error: e.message } }];
}

const propertyId = $('Initialize Agent Context').item.json.property_id;
const startTime = $('Initialize Agent Context').item.json.start_time;

if (!extractedData.document_type || !extractedData.confidence_score || extractedData.confidence_score < 0.5) {
  return [{ json: { skip: true, reason: 'Low confidence or missing document type' } }];
}

const costs = $('Initialize Agent Context').item.json.costs;
costs.firecrawl += 0.01;
costs.openrouter += 0.002;
costs.total = costs.firecrawl + costs.openrouter;

return [{
  json: {
    property_id: propertyId,
    document_type: extractedData.document_type,
    document_category: 'title',
    source: 'agent_retrieval',
    extracted_data: extractedData.extracted_data || extractedData,
    status: extractedData.confidence_score >= 0.7 ? 'validated' : 'needs_review',
    costs: costs,
    execution_time_ms: Date.now() - startTime
  }
}];

// Aggregate title results - handle both success and errors
const allItems = $input.all();
const documents = allItems.filter(i => !i.json.skip && !i.json.error).map(i => i.json.document_type);
const errors = allItems.filter(i => i.json.error || i.json.skip);
const totalCosts = allItems.reduce((acc, item) => {
  if (item.json.costs) {
    acc.firecrawl += item.json.costs.firecrawl || 0;
    acc.openrouter += item.json.costs.openrouter || 0;
  }
  return acc;
}, { firecrawl: 0, openrouter: 0, total: 0 });

totalCosts.total = totalCosts.firecrawl + totalCosts.openrouter;

const propertyId = $('Initialize Agent Context').item.json.property_id;
const startTime = $('Initialize Agent Context').item.json.start_time;

return [{
  json: {
    property_id: propertyId,
    status: documents.length > 0 ? 'success' : 'partial_failure',
    documents_retrieved: documents.length,
    document_types: documents,
    errors_count: errors.length,
    errors: errors.map(e => ({ reason: e.json.reason || e.json.error || 'Unknown error' })),
    costs: totalCosts,
    execution_time_ms: Date.now() - startTime,
    branch: 'title'
  }
}];
