// Generate Google search queries for VC/PE firms
const searchQueries = [
  'site:*.com "venture capital" "partners" email',
  'site:*.com "private equity" "team" email',
  'site:*.com "VC firm" "investment" email contact',
  'site:*.com "managing partner" "fund" email',
  'site:*.com "growth equity" "team" email contact',
  'site:*.com "family office" "investment" email'
];

return searchQueries.map((query, index) => ({
  json: {
    query: query,
    search_type: 'venture_capital_pe',
    query_id: `vcpe_${index + 1}`,
    timestamp: new Date().toISOString()
  }
}));

// Parse Google search results and extract URLs
const html = $json.body || '';

// Extract URLs from search results
const urlRegex = /https?:\/\/([\w\-]+\.)+[\w\-]+(\/[\w\-._~:/?#[\]@!$&'()*+,;=]*)?/g;
const urls = html.match(urlRegex) || [];

// Filter for VC/PE firm websites (exclude Google, social media, directories)
const companyUrls = urls.filter(url => 
  !url.includes('google.com') &&
  !url.includes('facebook.com') &&
  !url.includes('twitter.com') &&
  !url.includes('linkedin.com/company') &&
  (url.includes('.com') || url.includes('.net') || url.includes('.co')) &&
  (url.includes('capital') || url.includes('ventures') || url.includes('equity') || url.includes('partners') || url.includes('fund'))
);

// Deduplicate by domain
const uniqueDomains = [...new Set(companyUrls.map(url => {
  try {
    const domain = new URL(url).hostname;
    return domain;
  } catch {
    return null;
  }
}).filter(Boolean))];

// Return array of unique URLs to scrape
return uniqueDomains.slice(0, 10).map(domain => ({
  json: {
    url: `https://${domain}`,
    domain: domain,
    search_query: $json.query,
    discovered_at: new Date().toISOString()
  }
}));

// Extract emails, names, and titles from scraped pages
const teamPageHtml = $('Scrape Team Page').first().json.body || '';
const contactPageHtml = $('Scrape Contact Page').first().json.body || '';
const combinedHtml = teamPageHtml + ' ' + contactPageHtml;

// Email extraction
const emailRegex = /\b[A-Za-z0-9._%+-]+@[A-Za-z0-9.-]+\.[A-Z|a-z]{2,}\b/g;
const emails = combinedHtml.match(emailRegex) || [];

// Remove generic emails
const validEmails = emails.filter(email => 
  !email.includes('example.com') &&
  !email.includes('info@') &&
  !email.includes('support@') &&
  !email.includes('admin@') &&
  !email.includes('contact@') &&
  !email.includes('hello@')
);

// Extract names and titles (VC/PE specific patterns)
const namePatterns = [
  /([A-Z][a-z]+\s[A-Z][a-z]+)\s*[-–—,]\s*(Managing Partner|General Partner|Partner|Principal|Managing Director|Investment Director)/gi,
  /([A-Z][a-z]+\s[A-Z][a-z]+)\s*[•|]\s*(Managing Partner|General Partner|Partner|Principal|Managing Director)/gi
];

const prospects = [];

// Create prospect records for each unique email
const uniqueEmails = [...new Set(validEmails)];

uniqueEmails.forEach((email, index) => {
  prospects.push({
    prospect_id: `${$json.domain}_${index + 1}`,
    email: email,
    company_domain: $json.domain,
    company_website: $json.url,
    industry: 'venture_capital_pe',
    discovery_source: 'google_search_scraping',
    discovery_method: $json.search_query,
    discovered_at: new Date().toISOString(),
    enrichment_status: 'pending',
    data_quality_score: 30 // Base score for email discovery
  });
});

return prospects.map(prospect => ({ json: prospect }));

// Aggregate all discovered prospects
const items = $input.all();

const summary = {
  workflow: 'venture_capital_pe_discovery',
  prospects_saved: items.length,
  timestamp: new Date().toISOString()
};

return { json: summary };
